package com.yem.cloud.controller;import com.yem.cloud.feignservice.SealClient;import org.springframework.beans.factory.annotation.Value;import org.springframework.cloud.context.config.annotation.RefreshScope;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import javax.annotation.Resource;import java.time.LocalDateTime;import java.time.format.DateTimeFormatter;@RestController@RequestMapping("/apply")@RefreshScopepublic class ApplyController {	@Resource	private SealClient sealClient;	@Value("${pattern.dataformat}")	private String dateformat;	@GetMapping("/get/{id}")	public String now(@PathVariable String id){		return sealClient.getSeal(id);	}	@GetMapping("/add")	public String addSeal(){		SealVo sealVo = new SealVo();		sealVo.setId("12");		sealVo.setName("test");		return sealClient.addSeal(sealVo);	}}