package com.yem.cloud.feignservice;import com.yem.cloud.controller.SealVo;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;@FeignClient("sealservice")public interface SealClient {	@GetMapping("/seal/get/{id}")	String getSeal(@PathVariable("id") String id);	@PostMapping("/seal/add")	String addSeal(@RequestBody SealVo seal);}