package com.yem.cloud.seal.controller;import org.springframework.beans.factory.annotation.Value;import org.springframework.cloud.context.config.annotation.RefreshScope;import org.springframework.web.bind.annotation.*;import java.time.LocalDateTime;import java.time.format.DateTimeFormatter;@RestController@RequestMapping("/seal")@RefreshScopepublic class SealController {	private String dateformat = "yyyy-MM-dd";	@GetMapping("/get/{id}")	public String now(@PathVariable String id){		String format = LocalDateTime.now().format(DateTimeFormatter.ofPattern(dateformat));		return format + "-seal-" + id;	}	@PostMapping("/add")	public String addSeal(@RequestBody SealVo seal){		return seal.toString();	}}